var five = require("johnny-five");
var Edison = require("galileo-io");
var board = new five.Board({
  io: new Edison()
});

// console.log("1");
board.on("ready", function() {
  console.log("2");
  var sensor = new five.Sensor({
    pin: "A0",
    freq: 250
  });

  sensor.on("data", function() {
    console.log(this.value);
  });
});
